import React, { useState } from 'react';
import './Chatbot.css';

const Chatbot = () => {
  const [isOpen, setIsOpen] = useState(false);

  const toggleChat = () => {
    setIsOpen(!isOpen);
  };

  return (
    <div className="chatbot-container">
      {/* Chatbot Icon */}
      <div className="chatbot-icon" onClick={toggleChat}>
        {/* You can replace this with an image or SVG icon */}
        ðŸ’¬
      </div>

      {/* Chat Window */}
      <div className={`chat-window ${isOpen ? 'open' : 'closed'}`}>
        <div className="chat-header">
          <button className="back-button" onClick={toggleChat}>&larr;</button>
          <h2>Messages</h2>
        </div>
        <div className="chat-body">
          <div className="new-conversation-section">
            <h3>Start a new Chat</h3>
            <div className="new-conversation-input">
              <p>New Conversation</p>
              <span>We typically reply in a few minutes</span>
              <button>âž¤</button>
            </div>
          </div>

          <div className="recent-conversations-section">
            <h3>Recent</h3>
            {/* Placeholder for recent conversations list */}
            <div className="recent-conversation-item">
              <p>Customer Support</p>
              <span>Welcome to Elocate, if you need help simply...</span>
              <button>&rarr;</button>
            </div>
            {/* Add more recent conversation items here as needed */}
          </div>
        </div>
        <div className="chat-footer">
            {/* Placeholder for potential footer content like status or tawk.to link */}
            Powered by tawk.to
        </div>
      </div>
    </div>
  );
};

export default Chatbot;
