import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Toast from '../components/common/Toast';

const Profile = ({ user, token }) => {
  const [profile, setProfile] = useState(null);
  const [recyclingHistory, setRecyclingHistory] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [showToast, setShowToast] = useState(false);
  const [toastMessage, setToastMessage] = useState('');

  useEffect(() => {
    if (user && token) {
      fetchProfile();
      fetchRecyclingHistory();
    }
  }, [user, token]);

  const fetchProfile = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/users/profile', {
        headers: { Authorization: `Bearer ${token}` }
      });
      setProfile(response.data);
    } catch (err) {
      console.error('Profile fetch error:', err);
      setError('Failed to load profile information');
      setToastMessage('Failed to load profile information');
      setShowToast(true);
    }
  };

  const fetchRecyclingHistory = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/users/recycling-history', {
        headers: { Authorization: `Bearer ${token}` }
      });
      setRecyclingHistory(response.data);
    } catch (err) {
      console.error('Recycling history fetch error:', err);
      setError('Failed to load recycling history');
      setToastMessage('Failed to load recycling history');
      setShowToast(true);
    } finally {
      setLoading(false);
    }
  };

  if (!user) {
    return (
      <div className="profile-container">
        <h2>Please log in to view your profile</h2>
      </div>
    );
  }

  if (loading) {
    return (
      <div className="profile-container">
        <div className="loading">Loading profile...</div>
      </div>
    );
  }

  return (
    <div className="profile-container">
      {showToast && (
        <Toast
          message={toastMessage}
          type="error"
          onClose={() => setShowToast(false)}
        />
      )}
      
      <div className="profile-info">
        <h2>Profile Information</h2>
        {profile ? (
          <div>
            <p><strong>Name:</strong> {profile.name}</p>
            <p><strong>Email:</strong> {profile.email}</p>
            <p><strong>Address:</strong> {profile.address}</p>
          </div>
        ) : (
          <p>No profile information available</p>
        )}
      </div>

      <div className="recycling-history">
        <h2>Recycling History</h2>
        {recyclingHistory.length > 0 ? (
          <div className="history-list">
            {recyclingHistory.map((item, index) => (
              <div key={index} className="history-item">
                <p><strong>Date:</strong> {new Date(item.date).toLocaleDateString()}</p>
                <p><strong>Items:</strong> {item.items}</p>
                <p><strong>Status:</strong> {item.status}</p>
              </div>
            ))}
          </div>
        ) : (
          <p>No recycling history available</p>
        )}
      </div>
    </div>
  );
};

export default Profile;
