import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Map from '../components/Map';

const Facilities = () => {
  const [facilities, setFacilities] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [userLocation, setUserLocation] = useState(null);
  const [searchQuery, setSearchQuery] = useState('');
  const [filters, setFilters] = useState({
    location: '',
    wasteType: '',
    distance: 10,
    openNow: false
  });

  useEffect(() => {
    getUserLocation();
  }, []);

  useEffect(() => {
    if (userLocation) {
      fetchFacilities();
    }
  }, [filters, userLocation]);

  const getUserLocation = () => {
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(
        (position) => {
          const location = {
            lat: position.coords.latitude,
            lng: position.coords.longitude
          };
          setUserLocation(location);
          setFilters(prev => ({
            ...prev,
            location: `${location.lat},${location.lng}`
          }));
        },
        (error) => {
          console.error('Error getting location:', error);
          setError('Failed to get your location. Please enter a city name.');
        }
      );
    } else {
      setError('Geolocation is not supported by your browser. Please enter a city name.');
    }
  };

  const searchLocation = async (query) => {
    try {
      setLoading(true);
      // Using OpenStreetMap Nominatim API for geocoding
      const response = await axios.get(
        `https://nominatim.openstreetmap.org/search?format=json&q=${encodeURIComponent(query)}&limit=1`
      );
      
      if (response.data && response.data.length > 0) {
        const location = {
          lat: parseFloat(response.data[0].lat),
          lng: parseFloat(response.data[0].lon)
        };
        setUserLocation(location);
        setFilters(prev => ({
          ...prev,
          location: `${location.lat},${location.lng}`
        }));
        setError('');
      } else {
        setError('Location not found. Please try a different city name.');
      }
    } catch (err) {
      setError('Failed to search location. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  const handleSearch = (e) => {
    e.preventDefault();
    if (searchQuery.trim()) {
      searchLocation(searchQuery);
    }
  };

  const fetchFacilities = async () => {
    try {
      setLoading(true);
      const response = await axios.get('http://localhost:5000/api/facilities', {
        params: filters
      });
      setFacilities(response.data);
      setError('');
    } catch (err) {
      setError('Failed to fetch facilities. Please try again later.');
    } finally {
      setLoading(false);
    }
  };

  const handleFilterChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFilters(prev => ({
      ...prev,
      [name]: type === 'checkbox' ? checked : value
    }));
  };

  const handleMapClick = (latlng) => {
    setUserLocation(latlng);
    setFilters(prev => ({
      ...prev,
      location: `${latlng.lat},${latlng.lng}`
    }));
  };

  const formatHours = (hours) => {
    if (!hours) return 'Hours not available';
    
    const dayNames = {
      1: 'Monday',
      2: 'Tuesday',
      3: 'Wednesday',
      4: 'Thursday',
      5: 'Friday',
      6: 'Saturday',
      7: 'Sunday'
    };

    return hours.map(day => {
      const dayName = dayNames[day.day];
      return `${dayName}: ${day.open} - ${day.close}`;
    }).join(', ');
  };

  return (
    <div className="facilities-container">
      <section className="search-section">
        <h2>Find E-Waste Facilities</h2>
        <form onSubmit={handleSearch} className="search-form">
          <div className="search-input-group">
            <input
              type="text"
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
              placeholder="Enter city name (e.g., Madurai)"
              className="search-input"
            />
            <button type="submit" className="search-button">Search</button>
            <button type="button" onClick={getUserLocation} className="location-button">
              Use My Location
            </button>
          </div>
        </form>
        <div className="search-filters">
          <div className="filter-group">
            <select name="wasteType" value={filters.wasteType} onChange={handleFilterChange}>
              <option value="">All Waste Types</option>
              <option value="computers">Computers</option>
              <option value="phones">Phones</option>
              <option value="batteries">Batteries</option>
              <option value="appliances">Appliances</option>
            </select>
          </div>
          <div className="filter-group">
            <label>
              Distance (km):
              <input
                type="range"
                name="distance"
                min="1"
                max="50"
                value={filters.distance}
                onChange={handleFilterChange}
              />
              {filters.distance}km
            </label>
          </div>
          <div className="filter-group">
            <label>
              <input
                type="checkbox"
                name="openNow"
                checked={filters.openNow}
                onChange={handleFilterChange}
              />
              Open Now
            </label>
          </div>
        </div>
      </section>

      {error && <div className="error-message">{error}</div>}

      <Map 
        facilities={facilities}
        userLocation={userLocation}
        onMapClick={handleMapClick}
      />

      <section className="facilities-list">
        {loading ? (
          <div className="loading">Loading facilities...</div>
        ) : error ? (
          <div className="error">{error}</div>
        ) : facilities.length === 0 ? (
          <div className="no-results">No facilities found matching your criteria.</div>
        ) : (
          facilities.map(facility => (
            <div key={facility._id} className="facility-card">
              <h3>{facility.name}</h3>
              <p>{facility.location.address}</p>
              <div className="facility-details">
                <p>Distance: {facility.distance}km</p>
                <p>Types Accepted: {facility.wasteTypes.join(', ')}</p>
                <p>Hours: {formatHours(facility.operatingHours)}</p>
              </div>
              <div className="facility-actions">
                <a
                  href={`https://www.openstreetmap.org/directions?from=${userLocation?.lat},${userLocation?.lng}&to=${facility.location.coordinates[1]},${facility.location.coordinates[0]}`}
                  target="_blank"
                  rel="noopener noreferrer"
                  className="button"
                >
                  Get Directions
                </a>
                <a
                  href={`tel:${facility.contactInfo.phone}`}
                  className="button"
                >
                  Call Facility
                </a>
              </div>
            </div>
          ))
        )}
      </section>
    </div>
  );
};

export default Facilities;
