import React, { useState } from 'react';
import '../styles/main.css';
import Footer from '../components/Footer';

const blogContent = [
  // Same blog content as before
];

const Blogs = () => {
  const [showQuizModal, setShowQuizModal] = useState(false);

  const openQuizModal = () => setShowQuizModal(true);
  const closeQuizModal = () => setShowQuizModal(false);

  return (
    <div className="blogs-container">
      <h1>Harmful Effects of E-Device Waste</h1>
      {blogContent.map((item, idx) => (
        <div className={`blog-section ${idx % 2 === 0 ? 'right' : 'left'}`} key={idx}>
          <img src={item.image} alt={`Blog visual ${idx + 1}`} />
          <p>{item.paragraph}</p>
        </div>
      ))}
      <section className="Footer-section">
        <Footer />
      </section>
      <button className="quiz-button" onClick={openQuizModal}>
        Take the Quiz
      </button>
      {showQuizModal && (
        <div className="modal-overlay" onClick={closeQuizModal}>
          <div className="modal-content" onClick={(e) => e.stopPropagation()}>
            <h2>Interactive Quiz</h2>
            <p>Test your knowledge on the harmful effects of e-device waste.</p>
            <a
              href="https://forms.gle/exampleGoogleFormLink"
              target="_blank"
              rel="noopener noreferrer"
              className="quiz-link"
            >
              Go to Quiz
            </a>
            <button className="close-modal" onClick={closeQuizModal}>
              Close
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

export default Blogs;
