import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Facilities = () => {
  const [facilities, setFacilities] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [filters, setFilters] = useState({
    location: '',
    wasteType: '',
    distance: 10,
    openNow: false
  });

  useEffect(() => {
    fetchFacilities();
  }, [filters]);

  const fetchFacilities = async () => {
    try {
      setLoading(true);
      const response = await axios.get('http://localhost:5000/api/facilities', {
        params: filters
      });
      setFacilities(response.data);
      setError('');
    } catch (err) {
      setError('Failed to fetch facilities. Please try again later.');
    } finally {
      setLoading(false);
    }
  };

  const handleFilterChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFilters(prev => ({
      ...prev,
      [name]: type === 'checkbox' ? checked : value
    }));
  };

  const handleLocationSearch = async () => {
    if (navigator.geolocation) {
      try {
        const position = await new Promise((resolve, reject) => {
          navigator.geolocation.getCurrentPosition(resolve, reject);
        });
        setFilters(prev => ({
          ...prev,
          location: `${position.coords.latitude},${position.coords.longitude}`
        }));
      } catch (err) {
        setError('Failed to get your location. Please enter it manually.');
      }
    }
  };

  return (
    <div className="facilities-container">
      <section className="search-section">
        <h2>Find E-Waste Facilities</h2>
        <div className="search-filters">
          <div className="filter-group">
            <input
              type="text"
              name="location"
              value={filters.location}
              onChange={handleFilterChange}
              placeholder="Enter your location"
            />
            <button onClick={handleLocationSearch}>Use My Location</button>
          </div>
          <div className="filter-group">
            <select name="wasteType" value={filters.wasteType} onChange={handleFilterChange}>
              <option value="">All Waste Types</option>
              <option value="computers">Computers</option>
              <option value="phones">Phones</option>
              <option value="batteries">Batteries</option>
              <option value="appliances">Appliances</option>
            </select>
          </div>
          <div className="filter-group">
            <label>
              Distance (km):
              <input
                type="range"
                name="distance"
                min="1"
                max="50"
                value={filters.distance}
                onChange={handleFilterChange}
              />
              {filters.distance}km
            </label>
          </div>
          <div className="filter-group">
            <label>
              <input
                type="checkbox"
                name="openNow"
                checked={filters.openNow}
                onChange={handleFilterChange}
              />
              Open Now
            </label>
          </div>
        </div>
      </section>

      <section className="facilities-list">
        {loading ? (
          <div className="loading">Loading facilities...</div>
        ) : error ? (
          <div className="error">{error}</div>
        ) : facilities.length === 0 ? (
          <div className="no-results">No facilities found matching your criteria.</div>
        ) : (
          facilities.map(facility => (
            <div key={facility._id} className="facility-card">
              <h3>{facility.name}</h3>
              <p>{facility.address}</p>
              <div className="facility-details">
                <p>Distance: {facility.distance}km</p>
                <p>Types Accepted: {facility.wasteTypes.join(', ')}</p>
                <p>Hours: {facility.hours}</p>
              </div>
              <div className="facility-actions">
                <button onClick={() => window.open(`https://maps.google.com/?q=${facility.address}`, '_blank')}>
                  Get Directions
                </button>
                <button onClick={() => window.open(`tel:${facility.phone}`)}>
                  Call Facility
                </button>
              </div>
            </div>
          ))
        )}
      </section>
    </div>
  );
};

export default Facilities;
